bedrock = boto3.client('bedrock-runtime')
response = bedrock.invoke_model(
    modelId='anthropic.claude-3-5-sonnet-v2',
    body=json.dumps({
        'prompt': 'Hello, Claude!',
        'max_tokens': 100
    })
